import React, { useState } from 'react';
import SearchForm from './SearchingForm';
import CityWeatherInformation from './CityWeatherInformation';

const CityInfoList = () => {

    let [weatherInfo, setWeatherInfo] = useState([]);
    let [error, setError] = useState(false);
    let [loading, setLoading] = useState(false);

    const API_KEY = process.env.REACT_APP_OPENWEATHERMAP_API_KEY;

    function getWeatherInfo(city) {
        setLoading(true);
        setError(false);
        // clear state in preparation for new data
        setError(false);
        setWeatherInfo([]);
        setLoading(true);
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}`)
        .then(response => response.json())
        .then(data => {
            if (data.cod < 500) {
                throw new Error()

            } 
            setWeatherInfo([...weatherInfo, data]);
            setLoading(false);
        })
        .catch(error => {
            setError(true);
            setLoading(false);
            console.log(error.message);
        });
    }
    // const deleteCityInfo = (id) => {
    //     console.log(id)
    //     const newCityList = weatherInfo.filter((item) => item.id !== id);
    //     setWeatherInfo(newCityList)
    //  }
    return (
        <div className = 'container'>
            <h1>Weather</h1>
            <SearchForm getWeatherInfo = {getWeatherInfo} />
            <div className = 'weather-list'>
                <CityWeatherInformation 
                    weatherInfo={weatherInfo}
                    error={error}
                    loading={loading}
                />
            </div>
        </div>
    )
}
export default CityInfoList;
