import React from "react";


const SearchForm = ({ getWeatherInfo, setCity, city }) => {
    if(city.length <= 1)
        {
            document.getElementById('butt').disabled=true;
        } else { 
            document.getElementById('butt').disabled=false;
        }
        //Проверка полей
        function checkFields(form){
            if(form.name!="login") return false;
            var usr=form.elements['usr'].value;
            if(usr==''){
                alert('Имя не может быть пустым');
                form.elements['usr'].focus();
                return false;
            }
            return true;
        }

    return (
        <form onSubmit = {getWeatherInfo} disabled= {true}>
                <input
                    type = 'text'
                    placeholder = 'Search City'
                    maxLength = '50'
                    value = {city}
                    onChange = {(e) => setCity(e.target.value)}
                /> 
                <button className = 'buttonSearch' type = 'submit'>Search</button>
        </form>
    );
};

export default SearchForm;
