import React, { useState } from 'react';
import SearchForm from './SearchingForm';
import CityWeatherInformation from './CityWeatherInformation';

const CityInfoList = () => {
    let [weatherInfo, setWeatherInfo] = useState([]);
    let [error, setError] = useState(false);
    let [loading, setLoading] = useState(false);

    const API_KEY = process.env.REACT_APP_OPENWEATHERMAP_API_KEY;

    function getWeatherInfo(inputValue) {
        // clear state in preparation for new data
        setError(false);
        setLoading(true);
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${inputValue}&appid=${API_KEY}`)
        .then(response => response.json())
        .then(data => {
            if (data.cod !== 200) {
                throw new Error()
            }
            setCityWeathers((prevState) => [...prevState, res])
            setLoading(false);
        })
        .catch(error => {
            setError(true);
            setLoading(false);
            console.log(error.message);
        });
    }
    const weatherArray =  weatherInfo.map((city) => (
        <CityWeatherInformation key = {city.i} weatherInfo={city}
        error={error}
        loading={loading}/> 
        ))
    return (
        <div className = 'container'>
            <h1>Weather</h1>
            <SearchForm getWeatherInfo = {getWeatherInfo}/>
            <div className = 'weather-list'>
            {weatherArray}
                <CityWeatherInformation 
                    weatherInfo={weatherInfo}
                    error={error}
                    loading={loading}
                />
            </div>
        </div>
    )
}
export default CityInfoList;
