import React, { useState } from 'react';
import SearchForm from './SearchingForm';
import CityWeatherInformation from './CityWeatherInformation';

const CityInfoList = () => {
    let [weatherInfo, setWeatherInfo] = useState([]);
    let [error, setError] = useState(false);
    let [loading, setLoading] = useState(false);

    const API_KEY = process.env.REACT_APP_OPENWEATHERMAP_API_KEY;

    function getWeatherInfo(inputValue) {
        // clear state in preparation for new data
        if (inputValue)  return  setError("Please Enter the name of city.")
        setError(false);
        setLoading(true);
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${inputValue}&appid=${API_KEY}`)
        .then(response => response.json())
        .then(data => {
            if (data.cod !== 200) {
                throw new Error()
            }
            setWeatherInfo((prevState) => [...prevState, data])
            setLoading(false);
        })
        .catch(error => {
            setError(true);
            setLoading(false);
            console.log(error.message);
        });
    }

        function removeCity(key) {
            const newCityWeathers = weatherInfo.filter((weatherCity, index) => {
                if (index !== key) {
                    return true
                } else {
                    return false
                }
            })

            setWeatherInfo(newCityWeathers)
        }
    return (
        <div className = 'container'>
            <h1>Weather</h1>
            <SearchForm getWeatherInfo = {getWeatherInfo}/>
            <div className = 'weather-list'>

                <CityWeatherInformation 
                    weatherInfo={weatherInfo}
                    error={error}
                    loading={loading}
                    removeCity={removeCity} />
            </div>
        </div>
    )
}
export default CityInfoList;
